import Apis, { authApi, endpoints } from "configs/Apis";
import { useEffect, useState } from "react";
import { Button, Form, Image, ListGroup } from "react-bootstrap";
import Moment from "react-moment";
import { useParams } from "react-router-dom";
import Myspinner from "utils/formater";
import cookie from "react-cookies";

const Comment = () => {
    const { productId } = useParams();
    const [product, setProduct] = useState(null);
    const [comment, setComment] = useState(null);
    const [content, setContent] = useState();
    const [cartCounter, cartdispatch] = useContext(MyCartContext);
    const [products, setProducts] = useState(null);


    useEffect(() => {
        const loadProducts = async () => {
            try {
                let { data } = await Apis.get(endpoints["details"](productId));
                setProduct(data)
            } catch (ex) {
                console.error(ex)
            }
        }
        loadProducts()
    }, [])


    useEffect(() => {
        const loadComment = async () => {
            try {
                let { data } = await Apis.get(endpoints['comment-product'](productId));
                setComment(data);
            }
            catch (ex) {
                console.error(ex);
            }
        };

        loadComment();
    }, [comment]);


    const addReview = () => {
        const process = async () => {
            try {
                let { data } = await authApi().post(endpoints['addcomment'], {
                    "comment": content,
                    "productId": product.id,
                });
                setContent([data, ...content]);
                setContent('');
            }
            catch (ex) {
                console.error(ex)
            }
        }
        process();
    }
    const order = (products) => {
        cartdispatch({
          "type": "inc",
          "payload": 1
        });
        let cart = cookie.load("cart") || null
        if (cart === null)
          cart = {}
        if (products.id in cart) {
          // còn nếu có trong giỏ hàng thì tăng lên 1
          cart[products.id]['quantity'] += 1;
        } else {
          // nếu không có trong giỏ hàng
          cart[products.id] = {
            "id": products.id,
            "name": products.name,
            "quantity": 1,
            "unitPrice": products.price
          }
        }
        cookie.save("cart", cart);
        console.info(cart);
      }

    

    if (product === null)
        return <Myspinner />

    return (
        <>
            <div >
                <h1>{product.name}</h1>
                <h1>{product.description}</h1>
                <h1>{product.price} ₫</h1>
                <Image src={product.image}
                style={{
                    width: '600px',
                    height: '600px',
                    marginLeft: '30%',
                    marginTop: '5px'
                }} 
                    alt={product.name} />
            </div>
            <Button variant="success" onClick={() => order(p)} >Thêm vào giỏ hàng</Button>
            <hr />
                <Form.Control as="textarea" value={content} onChange={e => setContent(e.target.value)} rows={3} placeholder="Nội dung bình luận..." required />
                <p></p>
                <Button variant="info" onClick={addReview} className="mt-2 mb-2 ">Bình luận</Button>
            {comment === null ? <Myspinner /> : <>
                <ListGroup >
                    {comment.map(c => <ListGroup.Item key={c.reviewId}>
                        {c.userId.username} - {c.comment} - <Moment locale="vi" fromNow >{c.reviewDate}</Moment>  
                    </ListGroup.Item>)}
                </ListGroup>
            </>}
        </>
    )
}
export default Comment;
