import Apis, { endpoints } from "configs/Apis";
import { useEffect, useState } from "react";
import { Image } from "react-bootstrap";
import { useParams } from "react-router-dom";
import Myspinner from "utils/formater";

const Comment=() => {
    const {productId} = useParams();
    const [product,setProduct] = useState(null);

    useEffect(()=>{
        const loadProducts = async() => {
           try{ 
            let {data} = await Apis.get(endpoints["details"](productId));
           setProduct(data)
        }catch(ex){
            console.error(ex)
        }}
        loadProducts()
    },[])
    
    function test (){
        console.log('f:',product)
    }

    if(product === null)
        return <Myspinner/>

    return(
        <>
        <h1>helo: {productId}</h1>
        <h1>{product.name}</h1>
        <h1>{product.description}</h1>
        <h1>{product.price}</h1>
        <Image style={{width:'30px',height:'30px'}} src={product.image}
        alt={product.name}/>
        <button onClick={test}>dasd</button>
        </>
    )
}
export default Comment;
