import Apis, { authApi, endpoints } from "configs/Apis";
import { useEffect, useState, useContext } from "react";
import { Button, Form, Image, ListGroup } from "react-bootstrap";
import Moment from "react-moment";
import { useParams } from "react-router-dom";
import Myspinner from "utils/formater";
import "./Comment.css"

const Comment = () => {
    const { productId } = useParams();
    const [product, setProduct] = useState(null);
    const [comment, setComment] = useState(null);
    const [content, setContent] = useState();




    useEffect(() => {
        const loadProducts = async () => {
            try {
                let { data } = await Apis.get(endpoints["details"](productId));
                setProduct(data)
            } catch (ex) {
                console.error(ex)
            }
        }
        loadProducts()
    }, [])


    useEffect(() => {
        const loadComment = async () => {
            try {
                let { data } = await Apis.get(endpoints['comment-product'](productId));
                setComment(data);
            }
            catch (ex) {
                console.error(ex);
            }
        };

        loadComment();
    }, [comment]);


    const addReview = () => {
        const process = async () => {
            try {
                let { data } = await authApi().post(endpoints['addcomment'], {
                    "comment": content,
                    "productId": product.id,
                });
                setContent([data, ...content]);
                setContent('');
            }
            catch (ex) {
                console.error(ex)
            }
        }
        process();
    }

    if (product === null)
        return <Myspinner />

    return (
        <>
            <div>
                <div className="thongtinsp">
                    <h1>{product.name}</h1>
                    <h1>{product.description}</h1>
                    <h1>{product.price} ₫</h1>
                    <Image src={product.image}
                        alt={product.name} />
                </div>

            </div>
            <div className="binhluansp">
                {/* <Button variant="info" onClick={'#'} className="mt-2 mb-2 ">Bình luận</Button> */}
                <hr />
                <Form.Control as="textarea" value={content} onChange={e => setContent(e.target.value)} rows={3} placeholder="Nội dung bình luận..." required />
                <p></p>
                <Button variant="info" onClick={addReview} className="mt-2 mb-2 ">Bình luận</Button>
                {comment === null ? <Myspinner /> : <>
                    <ListGroup >
                        {comment.map(c => <ListGroup.Item key={c.reviewId}>
                            {c.userId.username}: {c.comment} - <Moment locale="vi" fromNow >{c.reviewDate}</Moment>
                        </ListGroup.Item>)}
                    </ListGroup>
                </>}
            </div>

        </>
    )
    // {user === null ? <p>Bạn phải <Link to="/dang-nhap-nguoi-dung?next=/gio-hang">đăng nhập</Link> để  có thể bình luận sản phẩm !!!</p> : <Button variant='info mt-2 mb-2' onClick={Comment}>Thanh Toán</Button>}
}
export default Comment;
