import Apis, { endpoints } from "configs/Apis";
import { useEffect, useState } from "react";
import { Image,Alert, Button, Form, ListGroup } from "react-bootstrap";
import { useParams } from "react-router-dom";
import Myspinner from "utils/formater";

const Comment = () => {
    const { productId } = useParams();
    const [product, setProduct] = useState(null);
    const [comment, setComment] = useState(null);

    useEffect(() => {
        const loadProducts = async () => {
            try {
                let { data } = await Apis.get(endpoints["details"](productId));
                setProduct(data)
            } catch (ex) {
                console.error(ex)
            }
        }
        loadProducts()
    }, [])

    // useEffect(() => {
    //     const loadComment = async () => {
    //         try {
    //             let { data } = await Apis.get(endpoints['commentproduct'](productId));
    //             setComment(data)
    //         } catch (ex) {
    //             console.error(ex)
    //         }
    //     }
    //     loadComment();
    // }, [comment])

    useEffect(() => {
        const loadComment = async () => {
            try {
                let { data } = await Apis.get(endpoints['comment-product'](productId));
                setComment(data);
            }
            catch (ex) {
                console.error(ex);
            }
        };

        loadComment();
    }, []);

    function test() {
        console.log('f:', product)
        console.log('c:', comment)
    }

    if (product === null)
        return <Myspinner />

    if (comment === null)
        //return <Myspinner />

        return (
            <>
                <h1>helo: {productId}</h1>
                <div >
                    <h1>{product.name}</h1>
                    <h1>{product.description}</h1>
                    <h1>{product.price}</h1>
                    <Image src={product.image}
                        alt={product.name} />
                </div>
                <hr />

                {reviews === null ? <Myspinner /> : <>
                    <ListGroup >
                        {reviews.map(c => <ListGroup.Item key={c.reviewId}>
                            {c.userId.username} - {c.comment} -
                        </ListGroup.Item>)}
                    </ListGroup>
                </>}

                <button onClick={test}>dasd</button>
            </>
        )
}
export default Comment;
